ESX = nil TriggerEvent('esx:getSharedObject', function(obj) ESX = obj end) RegisterServerEvent("W_admin:givecash") AddEventHandler("W_admin:givecash", function(money) local _source = source local xPlayer = ESX.GetPlayerFromId(_source) local total = money TriggerClientEvent('esx:showNotification', _source, "Give : ~g~+ "..total.."$ ~s~(Cash)") xPlayer.addMoney((total)) end) RegisterServerEvent("W_admin:givecashbank") AddEventHandler("W_admin:givecashbank", function(money) local _source = source local xPlayer = ESX.GetPlayerFromId(_source) local total = money TriggerClientEvent('esx:showNotification', _source, "Give : ~g~+ "..total.."$ ~s~(Banque)") xPlayer.addAccountMoney('bank', total) end) RegisterServerEvent("W_admin:givecashsale") AddEventHandler("W_admin:givecashsale", function(money) local _source = source local xPlayer = ESX.GetPlayerFromId(_source) local total = money TriggerClientEvent('esx:showNotification', _source, "Give : ~g~+ "..total.."$ ~s~(Sale)") xPlayer.addAccountMoney('black_money', total) end) RegisterServerEvent("W_admin:bringplayer") AddEventHandler("W_admin:bringplayer", function(plyId, plyPedCoords) TriggerClientEvent('W_admin:bringplayer', plyId, plyPedCoords) end) ESX.RegisterServerCallback('W_admin:getgroupadmin', function(source, cb) local xPlayer = ESX.GetPlayerFromId(source) if xPlayer ~= nil then local playerGroup = xPlayer.getGroup() if playerGroup ~= nil then cb(playerGroup) else cb(nil) end else cb(nil) end end)
